mapper.select:

select O_ID, O_CODE, O_NAME, O_DATE, O_COUNT, O_PRICE, O_TOTAL, O_STATUS
from ORDERCOMMAND
WHERE O_STATUS = #{o_status}



O_ID like '%'||#{searchValue}||'%'





	O_DATE between #{searchDate1} and #{searchDate2}


	O_DATE >= #{searchDate1}




	O_DATE between '00/01/01' and #{searchDate2}


select R_ID, R_NAME, R_DATE, R_CONTENTS, R_RATE, R_RECOMMEND from REVIEW
